    val listBatik: ArrayList<Batik>
        get() {
            val list = arrayListOf<Batik>()
            for (position in batikDetails.indices) {
                val btk = Batik()
                btk.name = batikNames[position].toString()
                btk.detail = batikDetails[position].toString()
                btk.images = batikImages[position]
                list.add(btk)
            }
            return list
        }
}

data class Batik(
    var name: String = "",
    var detail: String = "",
    var images: Int = 0
)