    val listTari: ArrayList<Tari>
        get() {
            val list = arrayListOf<Tari>()
            for (position in tariNames.indices) {
                val tari = Tari()
                tari.name = tariNames[position].toString()
                tari.detail = tariDetails[position].toString()
                tari.images = tariImages[position]
                list.add(tari)
            }
            return list
        }
